import React, { useState } from 'react';

const initialFormData = {
  name: '',
  age: '',
  currentLocation: '',
  gender: '',
  familyHistory: [],
  symptoms: [],
  symptomIntensity: {}
};

const familyHistoryOptions = [
  'Heart Attack', 'Asthma', 'Diabetes', 'Cardiac Arrest', 'Paralysis'
];

const symptomOptions = [
  'chest pain', 'fever', 'breathing difficulty', 'stomach ache'
];


function MultiStepForm() {
    const [error, setError] = useState('');
    
    const [isSubmitted, setIsSubmitted] = useState(false);

  const [formData, setFormData] = useState(initialFormData);
  const [currentStep, setCurrentStep] = useState(1);

  const handleFamilyHistoryChange = (event) => {
    const { value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      familyHistory: prevFormData.familyHistory.includes(value)
        ? prevFormData.familyHistory.filter((history) => history !== value)
        : [...prevFormData.familyHistory, value],
    }));
  };

  const handleSymptomChange = (event) => {
    if (!formData.symptoms.includes(event.target.value)) {
      setFormData({
        ...formData,
        symptoms: [...formData.symptoms, event.target.value],
        symptomIntensity: { ...formData.symptomIntensity, [event.target.value]: 1 }
      });
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleIntensityChange = (symptom, intensity) => {
    setFormData({
      ...formData,
      symptomIntensity: { ...formData.symptomIntensity, [symptom]: intensity }
    });
  };

  const renderErrorMessage = () => {
    return error ? <div style={{ color: 'red' }}>{error}</div> : null;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentStep < 6) {
      nextStep();
    } else {
      // Ensure all required fields are filled in the last step (implement similar checks as above)
      if (formData.symptoms.length === 0) { // Example check
        setError('Please select at least one symptom.');
        return;
      }
      console.log("Form Data Submitted:", JSON.stringify(formData, null, 2));
      setIsSubmitted(true);
      setError(''); // Reset error message on successful submission
    }
  };
  
  const validateCurrentStep = () => {
    switch (currentStep) {
      case 1:
        if (!formData.name.trim()) {
          setError('Name is required');
          return false;
        }
        break;
      case 2:
        if (!formData.age.trim()) {
          setError('Age is required');
          return false;
        }
        break;
      case 3:
        if (!formData.currentLocation.trim()) {
          setError('Current location is required');
          return false;
        }
        break;
      case 4:
        if (!formData.gender) {
          setError('Gender is required');
          return false;
        }
        break;
      // Additional cases for other steps as needed...
    }
    setError('');
    return true;
  };

  const nextStep = () => {
    if (validateCurrentStep()) {
      setCurrentStep((prevStep) => prevStep + 1);
    }
  };

  const prevStep = () => {
    setCurrentStep((prevStep) => prevStep - 1);
  };

  // Helper function to render the current step
  const renderStep = () => {
    switch (currentStep) {
        case 1:
            return (
                <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Enter your name"
                />
            );
        case 2:
            return (
                <input
                type="number"
                name="age"
                value={formData.age}
                onChange={handleInputChange}
                placeholder="Enter your age"
                />
            );
        case 3:
            return (
                <input
                type="text"
                name="currentLocation"
                value={formData.currentLocation}
                onChange={handleInputChange}
                placeholder="Enter your current location"
                />
            );
        case 4:
            return (
                <select
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
                >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
                </select>
            );
        case 5:
            return (
                <div>
                <p>Family history of:</p>
                {familyHistoryOptions.map((condition, index) => (
                    <label key={index}>
                    <input
                        type="checkbox"
                        value={condition}
                        onChange={handleFamilyHistoryChange}
                        checked={formData.familyHistory.includes(condition)}
                    />
                    {condition}
                    </label>
                ))}
                <div>Selected Conditions: {formData.familyHistory.join(', ')}</div>
                </div>
            );
            case 6:
                // Calculate the progress based on symptoms selected
                const symptomProgress = (formData.symptoms.length / symptomOptions.length) * 100;
                return (
                  <div>
                    <p>Select your symptoms (Progress: {symptomProgress.toFixed(0)}% towards better outcome):</p>
                    {symptomOptions.map(symptom => (
                      <div key={symptom}>
                        <label>
                          <input
                            type="checkbox"
                            value={symptom}
                            onChange={handleSymptomChange}
                            checked={formData.symptoms.includes(symptom)}
                          />
                          {symptom}
                        </label>
                        {formData.symptoms.includes(symptom) && (
                          <input
                            type="range"
                            min="1"
                            max="5"
                            value={formData.symptomIntensity[symptom]}
                            onChange={(e) => handleIntensityChange(symptom, e.target.value)}
                          />
                        )}
                      </div>
                    ))}
                    <div className="progress-container" style={{marginTop: "20px"}}>
                      <div className="progress-bar" style={{width: `${symptomProgress}%`, backgroundColor: 'lightblue', height: '20px'}}></div>
                    </div>
                  </div>
                );              
        // ... handle the final step
        default:
            return null;
        }
    };

    

    return (
        <div>
          {isSubmitted ? (
            <div className="success-message">
              Successfully submitted
            </div>
          ) : (
            <form onSubmit={handleSubmit}>
              {renderStep()}
              {renderErrorMessage()} {/* Display the error message if there's an error */}
              <div>
                {currentStep > 1 && <button type="button" onClick={prevStep}>Back</button>}
                {currentStep < 6 && <button type="button" onClick={() => nextStep()}>Next</button>}
                {currentStep === 6 && <button type="submit">Submit</button>}
              </div>
            </form>
          )}
          <div className="progress-bar">
            <div style={{ width: `${(currentStep / 6) * 100}%` }} />
          </div>
        </div>
      );      
}

export default MultiStepForm;
