import React, { useState } from 'react';

const initialFormData = {
  name: '',
  age: '',
  currentLocation: '',
  gender: '',
  familyHistory: [],
  symptoms: [],
  symptomIntensity: {}
};

const familyHistoryOptions = [
  'Heart Attack', 'Asthma', 'Diabetes', 'Cardiac Arrest', 'Paralysis'
];

const symptomOptions = [
  'chest pain', 'fever', 'breathing difficulty', 'stomach ache'
];

function MultiStepForm() {
  const [formData, setFormData] = useState(initialFormData);
  const [currentStep, setCurrentStep] = useState(1);
  const [isSubmitted, setIsSubmitted] = useState(false); // Moved inside the component

  const handleFamilyHistoryChange = (event) => {
    const { value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      familyHistory: prevFormData.familyHistory.includes(value)
        ? prevFormData.familyHistory.filter((history) => history !== value)
        : [...prevFormData.familyHistory, value],
    }));
  };

  const handleSymptomChange = (event) => {
    if (!formData.symptoms.includes(event.target.value)) {
      setFormData({
        ...formData,
        symptoms: [...formData.symptoms, event.target.value],
        symptomIntensity: { ...formData.symptomIntensity, [event.target.value]: 1 }
      });
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleIntensityChange = (symptom, intensity) => {
    setFormData({
      ...formData,
      symptomIntensity: { ...formData.symptomIntensity, [symptom]: intensity }
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitted(true); // This will now work correctly
    // Removed the console log and alert based on your request
  };

  // Other code remains the same

  return (
    <div>
        {isSubmitted ? (
        <div className="success-message">
            Successfully submitted
        </div>
        ) : (
        <form onSubmit={handleSubmit}>
            {renderStep()}
            <div>
            {currentStep > 1 && <button type="button" onClick={prevStep}>Back</button>}
            {currentStep < 6 && <button type="button" onClick={nextStep}>Next</button>}
            {currentStep === 6 && <button type="submit">Submit</button>}
            </div>
        </form>
        )}
        <div className="progress-bar">
        <div style={{ width: `${(currentStep / 6) * 100}%` }} />
        </div>
    </div>
  );
}      

export default MultiStepForm;
