import React, { useState } from 'react';

const initialFormData = {
  name: '',
  age: '',
  currentLocation: '',
  gender: '',
  familyHistory: [],
  symptoms: [],
  symptomIntensity: {}
};

const [isSubmitted, setIsSubmitted] = useState(false);


const familyHistoryOptions = [
  'Heart Attack', 'Asthma', 'Diabetes', 'Cardiac Arrest', 'Paralysis'
];

const symptomOptions = [
  'chest pain', 'fever', 'breathing difficulty', 'stomach ache'
];

function MultiStepForm() {
  const [formData, setFormData] = useState(initialFormData);
  const [currentStep, setCurrentStep] = useState(1);

  const handleFamilyHistoryChange = (event) => {
    const { value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      familyHistory: prevFormData.familyHistory.includes(value)
        ? prevFormData.familyHistory.filter((history) => history !== value)
        : [...prevFormData.familyHistory, value],
    }));
  };

  const handleSymptomChange = (event) => {
    if (!formData.symptoms.includes(event.target.value)) {
      setFormData({
        ...formData,
        symptoms: [...formData.symptoms, event.target.value],
        symptomIntensity: { ...formData.symptomIntensity, [event.target.value]: 1 }
      });
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleIntensityChange = (symptom, intensity) => {
    setFormData({
      ...formData,
      symptomIntensity: { ...formData.symptomIntensity, [symptom]: intensity }
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitted(true); // Set the flag to true to indicate successful submission
    // Here, you would typically send formData to your server via an API call
  };
  

  const nextStep = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const prevStep = () => {
    setCurrentStep((prevStep) => prevStep - 1);
  };

  // Helper function to render the current step
  const renderStep = () => {
    switch (currentStep) {
        case 1:
            return (
                <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Enter your name"
                />
            );
        case 2:
            return (
                <input
                type="number"
                name="age"
                value={formData.age}
                onChange={handleInputChange}
                placeholder="Enter your age"
                />
            );
        case 3:
            return (
                <input
                type="text"
                name="currentLocation"
                value={formData.currentLocation}
                onChange={handleInputChange}
                placeholder="Enter your current location"
                />
            );
        case 4:
            return (
                <select
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
                >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
                </select>
            );
        case 5:
            return (
                <div>
                <p>Family history of:</p>
                {familyHistoryOptions.map((condition, index) => (
                    <label key={index}>
                    <input
                        type="checkbox"
                        value={condition}
                        onChange={handleFamilyHistoryChange}
                        checked={formData.familyHistory.includes(condition)}
                    />
                    {condition}
                    </label>
                ))}
                <div>Selected Conditions: {formData.familyHistory.join(', ')}</div>
                </div>
            );
        case 6:
            return (
                <div>
                {symptomOptions.map(symptom => (
                    <div key={symptom}>
                    <label>
                        <input
                        type="checkbox"
                        value={symptom}
                        onChange={handleSymptomChange}
                        checked={formData.symptoms.includes(symptom)}
                        />
                        {symptom}
                    </label>
                    {formData.symptoms.includes(symptom) && (
                        <input
                        type="range"
                        min="1"
                        max="5"
                        value={formData.symptomIntensity[symptom]}
                        onChange={(e) => handleIntensityChange(symptom, e.target.value)}
                        />
                    )}
                    </div>
                ))}
                </div>
            );
        // ... handle the final step
        default:
            return null;
        }
    };

    

  return (
    <div>
      <form onSubmit={(e) => e.preventDefault()}>
        {renderStep()}
        <div>
          {currentStep > 1 && <button onClick={prevStep}>Back</button>}
          {currentStep < 6 && <button onClick={nextStep}>Next</button>}
          {currentStep === 6 && <button onClick={handleSubmit}>Submit</button>}
        </div>
      </form>
      <div className="progress-bar">
        <div style={{ width: `${(currentStep / 6) * 100}%` }} />
      </div>
    </div>
  );
}

export default MultiStepForm;
